const ALERT=document.getElementById("alert"),CONTENTALERT=document.getElementById("alert__bg"),COUNTBUTTON=document.getElementById("btn"),EXITBUTTON=document.getElementById("exit__alert"),RESETBUTTON=document.getElementById("reset__counter");let CLICK=localStorage.getItem("counter");COUNTBUTTON.addEventListener("click",()=>{ALERT.classList.toggle("active"),CLICK++,localStorage.setItem("counter",CLICK),document.getElementById("counter").innerHTML=CLICK,console.log(CLICK),5<CLICK?RESETBUTTON.classList.add("active"):RESETBUTTON.classList.remove("active")}),EXITBUTTON.addEventListener("click",()=>{ALERT.classList.remove("active")}),RESETBUTTON.addEventListener("click",()=>{CLICK=0,localStorage.removeItem("counter"),ALERT.classList.remove("active")}),ALERT.addEventListener("click",e=>{e.target===CONTENTALERT&&ALERT.classList.remove("active")});